import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { addFav, removeFav } from "../../redux/actions";
import { useEffect, useState } from "react";
import { connect } from "react-redux";

function Card({id,name,status,species,gender,origin,image,onClose,remove_Fav,add_Fav,myFavorites,}) {
  const [isFav, setIsFav] = useState(false);


  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      remove_Fav(id);
    } else {
      setIsFav(true);
      add_Fav({ id, name, status, species, gender, origin, image });
    }
  };

  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [myFavorites]);

  return (
    <div className={style.conteiner}>
      <div className={style.extraButtons}>
        <button onClick={handleFavorite} className={style.heart}>
          {isFav ? "‚ù§Ô∏è" : "ü§ç"}
        </button>
        <button className={style.button} onClick={() => onClose(id)}>
          X
        </button>
      </div>
      {image ? <img src={image} alt="{name}"/> : <img src="https://upload.wikimedia.org/wikipedia/commons/b/b9/Youtube_loading_symbol_1_(wobbly).gif" alt="Loading" id={style.loading}/>} 
      <Link to={`/detail/${id}`}>
        <h2>{name}</h2>
      </Link>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    add_Fav: (character) => {
      dispatch(addFav(character));
    },
    remove_Fav: (id) => {
      dispatch(removeFav(id));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
